import './App.css';
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import Box from '@mui/material/Box';
import React, {useState} from 'react';


function App() {
  const [voltValue, setVoltValue] = useState();
  const [ampValue, setAmpValue] = useState();
  const [ohmValue, setOhmValue] = useState();
  const [wattValue, setWattValue] = useState();

  return (
    <div className="App">
      <header className='Header'>Ohm's Law Calculator</header>
      <div className='Parent'>
        <Box m={10}>

            <TextField id="v" label="Volts" variant="filled" onChange={readValue} value={voltValue}/>
            <TextField id="a" label="Ampere" variant="filled" onChange={readValue} value={ampValue}/>
            <TextField id="o" label="Ohms" variant="filled" onChange={readValue} value={ohmValue}/>
            <TextField id="w" label="Watts" variant="filled" onChange={readValue} value={wattValue}/>

        </Box>


        <div className='Buttons'>
          <Button variant="contained" onClick={calculate}>Calculate</Button>
        </div>
      </div>

    </div>
  );


  function readValue(e) {
    let value = e.target.value;
    let id = e.target.id;

    if (id == "v") {
      setVoltValue(value);
    }


    else if (id == "a") {
      setAmpValue(value);
    }


    else if (id == "o") {
      setOhmValue(value);
    }


    else if (id == "w") {
      setWattValue(value);
    }

  }

  function calculate() {

    if (voltValue != null && ampValue != null) {
        setOhmValue(parseFloat(voltValue / ampValue).toFixed(2));
        setWattValue(parseFloat(voltValue * ampValue).toFixed(2));
    }

    if (ohmValue != null && ampValue != null) {
        setVoltValue(ohmValue * ampValue);
        setWattValue(Math.pow(ampValue, 2) * ohmValue);
    }




  }


}

export default App;
