import './App.css';
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import Box from '@mui/material/Box';
import React, {useState} from 'react';


function App() {
  const [voltValue, setVoltValue] = useState(0);
  const [ampValue, setAmpValue] = useState(0);
  const [ohmValue, setOhmValue] = useState(0);
  const [wattValue, setWattValue] = useState(0);
  return (
    <div className="App">
      <header className='Header'>Ohm's Law Calculator</header>
      <div className='Parent'>
        <Box m={10}>

            <TextField id="v" label="Volts" variant="filled" onChange={readValue}/>
            <TextField id="a" label="Ampere" variant="filled" onChange={readValue}/>
            <TextField id="o" label="Ohms" variant="filled" onChange={readValue}/>
            <TextField id="w" label="Watts" variant="filled" onChange={readValue}/>

        </Box>


        <div className='Buttons'>
          <Button variant="contained">Calculate</Button>
        </div>
      </div>

    </div>
  );

  function readValue(e) {

    let id = e.target.id;
    let value;

    if (id = "v") {
      value = e.target.value;
      setVoltValue(value);
    }

    if (id = "a") {
      value = e.target.value;
      setAmpValue(value);
    }

    if (id = "o") {
      value = e.target.value;
      setOhmValue(value);
    }

    if (id = "w") {
      value = e.target.value;
      setWattValue(value);
    }

    console.log(voltValue);
    console.log(ampValue);
    console.log(ohmValue);
    console.log(wattValue);
  }
}

export default App;
