import './App.css';
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import Box from '@mui/material/Box';
import React, {useState} from 'react';


function App(key) {
  const [voltValue, setVoltValue] = useState("");
  const [ampValue, setAmpValue] = useState("");
  const [ohmValue, setOhmValue] = useState("");
  const [wattValue, setWattValue] = useState("");

  const [border1, setBorder1] = useState({border: '5px solid black'});
  const [border2, setBorder2] = useState({border: '5px solid black'});
  const [border3, setBorder3] = useState({border: '5px solid black'});
  const [border4, setBorder4] = useState({border: '5px solid black'});

  const [buttonLabel, setButtonLabel] = useState("Calculate");
  return (
    <div className="App">
      <header className='Header'>Ohm's Law</header>
      <div className='Parent'>
        <Box m={10}>

            <TextField id="v" autoComplete='off' style={border1} InputLabelProps={{shrink: true}} label="Volts" variant="filled" onChange={readValue} value={voltValue}/>
            <br></br>
            <br></br>
            <TextField id="a" autoComplete='off' style={border2} InputLabelProps={{shrink: true}} label="Ampere" variant="filled" onChange={readValue} value={ampValue}/>
            <br></br>
            <br></br>
            <TextField id="o" autoComplete='off' style={border3} InputLabelProps={{shrink: true}} label="Ohms" variant="filled" onChange={readValue} value={ohmValue}/>
            <br></br>
            <br></br>
            <TextField id="w" autoComplete='off' style={border4} InputLabelProps={{shrink: true}} label="Watts" variant="filled" onChange={readValue} value={wattValue}/>

        </Box>


        <div className='Buttons'>
          <Button variant="contained" onClick={calculate}>{buttonLabel}</Button>
        </div>
      </div>

    </div>
  );


  function readValue(e) {

    let value = e.target.value;
    let id = e.target.id;

    if (id == "v") {
      setVoltValue(value);
    }


    else if (id == "a") {
      setAmpValue(value);
    }


    else if (id == "o") {
      setOhmValue(value);
    }


    else if (id == "w") {
      setWattValue(value);
    }

  }

  function calculate() {

    if (buttonLabel == "Calculate") {
      if (voltValue != "" && ampValue != "") {
        setOhmValue(parseFloat(voltValue / ampValue).toFixed(2));
        setWattValue(parseFloat(voltValue * ampValue).toFixed(2));

        setBorder3({border: '5px solid orange'});
        setBorder4({border: '5px solid orange'});
    }

    else if (voltValue != "" && ohmValue != "") {
        setAmpValue(parseFloat(voltValue / ohmValue).toFixed(2));
        setWattValue(parseFloat(Math.pow(voltValue, 2) * ohmValue).toFixed(2));

        setBorder2({border: '5px solid orange'});
        setBorder4({border: '5px solid orange'});
    }

    else if (voltValue != "" && wattValue != "") {
      setAmpValue(parseFloat(wattValue / voltValue).toFixed(2));
      setOhmValue(parseFloat(Math.pow(voltValue, 2) / wattValue).toFixed(2));

      setBorder2({border: '5px solid orange'});
      setBorder3({border: '5px solid orange'});
    }

    else if (ampValue != "" && wattValue != "") {
      setVoltValue(parseFloat(wattValue / ampValue).toFixed(2));
      setOhmValue(parseFloat(wattValue / Math.pow(ampValue, 2)).toFixed(2));

      setBorder1({border: '5px solid orange'});
      setBorder3({border: '5px solid orange'});
    }

    else if (ohmValue != "" && ampValue != "") {
        setVoltValue(parseFloat(ohmValue * ampValue).toFixed(2));
        setWattValue(parseFloat(Math.pow(ampValue, 2) * ohmValue).toFixed(2));

        setBorder1({border: '5px solid orange'});
        setBorder4({border: '5px solid orange'});
    }

    else if (ohmValue != "" && wattValue != "") {
        setVoltValue(parseFloat(Math.sqrt(wattValue * ohmValue)).toFixed(2));
        setAmpValue(parseFloat(Math.sqrt(wattValue / ohmValue)).toFixed(2));

        setBorder1({border: '5px solid orange'});
        setBorder2({border: '5px solid orange'});
    }

    setButtonLabel("Reset");
    }

    else if (buttonLabel == "Reset") {

      setVoltValue("");
      setAmpValue("");
      setOhmValue("");
      setWattValue("");

      setBorder1({border: '5px solid black'});
      setBorder2({border: '5px solid black'});
      setBorder3({border: '5px solid black'});
      setBorder4({border: '5px solid black'});

      setButtonLabel("Calculate");
    }

  }


}

export default App;
